INSERT INTO `kiriman` (`quote`, `id`, `kiriman`, `sumber`, `status`, `path`, `catatan`, `tanggal_upload`, `created_at`, `created_by`, `modified_at`, `modified_by`) VALUES
('Ta''lim itu niatnya cari ilmu kalau pas dapat jodoh itu bonus.', 15, 'â€œSesungguhnya setiap amalan tergantung pada niatnya. Setiap orang akan mendapatkan apa yang ia niatkan. Siapa yang hijrahnya karena Allah dan Rasul-Nya, maka hijrahnya untuk Allah dan Rasul-Nya. Siapa yang hijrahnya karena mencari dunia atau karena wanita yang dinikahinya, maka hijrahnya kepada yang ia tuju.â€', '[HR. Bukhari, no. 1 dan Muslim, no. 1907]', NULL, NULL, NULL, NULL, 1509664076, 4, 1509664076, 4),
('pondasi islam itu lima perkara. syahadat, shalat, zakat, puasa dan haji', 16, 'â€Islam itu engkau bersaksi bahwa sesungguhnya tiada Tuhan selain Alloh dan sesungguhnya Muhammad itu utusan Alloh, engkau mendirikan sholat, mengeluarkan zakat, berpuasa pada bulan Romadhon dan mengerjakan ibadah haji ke Baitullah jika engkau mampu melakukannya.â€', 'HR Muslim', NULL, NULL, NULL, NULL, 1509674405, 4, 1509674405, 4),
('6 yang terpenting dalam iman', 17, 'Rasulullah menjawab,â€Engkau beriman kepada Alloh, kepada para Malaikat-Nya, Kitab-kitab-Nya, kepada utusan-utusan Nya, kepada hari Kiamat dan kepada takdir yang baik maupun yang burukâ€', 'HR Muslim', NULL, NULL, NULL, NULL, 1509674473, 4, 1509674473, 4),
('Ibadahlah seakan akan engkau melihat Allah', 18, 'Nabi shallallahu â€˜alaihi wa sallam bersabda: â€œ(Ihsan adalah) Engkau beribadah kepada Allah seolah-olah engkau melihat-Nya. Kalaupun engkau tidak bisa melihat-Nya, sungguh Diamelihatmu.â€', 'HR Muslim', NULL, '3N7-08.png', NULL, 1509719492, 1509674695, 4, 1509719492, 2),
('salah satu tanda kiamat adalah banyaknya gedung-gedung tinggi.', 19, 'Burj Khalifa memiliki tinggi 868 meter atau 2.717 meter di atas permukaan laut.\n\nNabi shallallahu â€˜alaihi wa sallam berkata: â€œJika seorang budak wanita melahirkan tuannya dan jika engkau mendapati penggembala kambing yang tidak beralas kaki dan tidak pakaian saling berlomba dalam meninggikan bangunan.â€', 'HR Muslim', NULL, '3N7-07.png', NULL, 1509718675, 1509674871, 4, 1509718675, 2),
('jangan takut soal rezeki. Allah sudah menjaminnya', 20, 'Kemudian diutus seorang malaikat, lalu dia meniupkan ruh kepadanya, dan dia (malaikat tadi) diperintah untuk menulis 4 kalimat (perkara): tentang rezekinya, amalannya, ajalnya dan (apakah) dia termasuk orang yang sengsara atau bahagia.', 'HR Bukhari dan Muslim', NULL, '3N7-06.png', NULL, 1509717747, 1509676786, 4, 1509717747, 2),
('Ya Allah teguhkan kami di agamamu', 21, 'â€œWahai Robb yang membolak-balikkan hati, teguhkanlah hatiku di atas agama-Mu.â€', 'HR.Tirmidzi 3522, Ahmad 4/302, al-Hakim 1/525, Lihat Shohih Sunan Tirmidzi III no.2792', NULL, '3N7-05.png', NULL, 1509716389, 1509677583, 4, 1509716389, 2),
('Islam sudah sempurna. emang motor perlu modif biar keren', 22, 'â€œBarangsiapa yang (memulai) mengada-adakan (sesuatu yang baru) dalam urusan (agama) kami ini yang bukan termasuk bagian darinya, maka amalan tersebut tertolak.â€', 'HR. Bukhari dan Muslim', NULL, '3N7-04.png', NULL, 1509715337, 1509678397, 4, 1509715337, 2),
('Halal haram itu seperti siang dan malam. Jelas Bedanya', 23, 'Dari Abu â€˜Abdillah Nuâ€™man bin Basyir radhiyallahu â€˜anhu, ia berkata: Aku mendengar Rasulullah shallallahu â€˜alaihi wa sallam bersabda: â€Sesungguhnya perkara yang halal itu telah jelas dan perkara yang haram itu telah jelas.', 'Bukhari no. 52, 2051 dan Muslim no. 1599', NULL, '3N7-03.png', 'kayaknya perlu ilustrasi siang dan malam wan', 1509714620, 1509678841, 4, 1509831346, 4),
('Segumpal daging kebaikan', 24, 'Ketahuilah bahwa dalam tubuh manusia terdapat sepotong daging. Apabila daging tersebut baik maka baik pula seluruh tubuhnya dan apabila daging tersebut rusak maka rusak pula seluruh tubuhnya. Ketahuilah segumpal daging tersebut adalah kalbu (hati).', 'HR. Al-Bukhari dan Muslim', NULL, '3N7-02.png', NULL, 1509712408, 1509678971, 4, 1509712408, 2),
('Agama itu nasihat', 25, 'Dari Abu Ruqayyah Tamim bin Aus Ad Dari radhiyallahu â€˜anhu, dia berkata: â€œSesungguhnya Rasulullah shallallahu â€˜alaihi wa sallam bersabda: â€Agama itu nasihat.â€ Kami bertanya: â€Untuk siapa?â€ Beliau shallallahu â€˜alaihi wa sallam menjawab: â€Untuk Allah, untuk kitab-Nya, untuk Rasul-Nya, untuk pemimpin kaum muslimin dan seluruh kaum muslimin.â€', 'Diriwayatkan oleh Muslim (no 55', NULL, 'image4493.png', NULL, 1509712616, 1509679079, 4, 1509712616, 2),
('Mau didoakan Nabi? Jangan Tidur di waktu pagi', 26, 'Dari sahabat Shokhr Al Ghomidiy, Nabi shallallahu â€˜alaihi wa sallam bersabda,\n\nâ€œYa Allah, berkahilah umatku di waktu paginya.â€', NULL, NULL, '6N7-01.png', 'Ilustrasi matahari terbit', 1509938230, 1509831691, 4, 1509938230, 2),
('Ingin Kaya, Mulailah Bekerja Saat Pagi', 27, 'Apabila Nabi shallallahu mengirim peleton pasukan, beliau shallallahu â€˜alaihi wa sallam mengirimnya pada pagi hari. Sahabat Shokhr sendiri (yang meriwayatkan hadits ini, pen) adalah seorang pedagang. Dia biasa membawa barang dagangannya ketika pagi hari. Karena hal itu dia menjadi kaya dan banyak harta. Abu Daud mengatakan bahwa dia adalah Shokhr bin Wadaâ€™ah.', NULL, NULL, '6N7-02.png', NULL, 1509948408, 1509831819, 4, 1509948408, 2),
('Setelah Shalat Subuh Jangan Pergi. duduklah berdzikir hingga matahari terbit', 28, 'Beliau shallallahu â€˜alaihi wa sallam biasanya tidak beranjak dari tempat duduknya setelah shalat shubuh hingga terbit matahari. Apabila matahari terbit, beliau shallallahu â€˜alaihi wa sallam berdiri (meninggalkan tempat shalat). Dulu para sahabat biasa berbincang-bincang (guyon) mengenai perkara jahiliyah, lalu mereka tertawa. Sedangkan beliau shallallahu â€˜alaihi wa sallam hanya tersenyum saja.â€', '(HR. Muslim no. 670)', NULL, '6N7-03.png', NULL, 1509949459, 1509832613, 4, 1510201283, 5),
('sholat itu menghadap Allah. Bukan balapan yang butuh cepet-cepetan', 29, 'Dari Abu Hurairah, Nabi shallallahu â€˜alaihi wa sallam ketika masuk masjid, maka masuklah seseorang lalu ia melaksanakan shalat. Setelah itu, ia datang dan memberi salam pada Nabi shallallahu â€˜alaihi wa sallam, lalu beliau menjawab salamnya. Beliau berkata, â€œUlangilah shalatmu karena sesungguhnya engkau tidaklah shalat.â€ Lalu ia pun shalat dan datang lalu memberi salam pada Nabi shallallahu â€˜alaihi wa sallam. Beliau tetap berkata yang sama seperti sebelumnya, â€œUlangilah shalatmu karena sesungguhnya engkau tidaklah shalat.â€ Sampai diulangi hingga tiga kali. Orang yang jelek shalatnya tersebut berkata, â€œDemi yang mengutusmu membawa kebenaran, aku tidak bisa melakukan shalat sebaik dari itu. Makanya ajarilah aku!â€ Rasulullah shallallahu â€˜alaihi wa sallam lantas mengajarinya dan bersabda, â€œJika engkau hendak shalat, maka bertakbirlah. Kemudian bacalah ayat Al Qurâ€™an yang mudah bagimu. Lalu rukuâ€™lah dan sertai thumaâ€™ninah ketika rukuâ€™. Lalu bangkitlah dan beriâ€™tidallah sambil berdiri. Kemudian sujudlah sertai thumaâ€™ninah ketika sujud. Kemudian bangkitlah dan duduk antara dua sujud sambil thumaâ€™ninah. Kemudian sujud kembali sambil disertai thumaâ€™ninah ketika sujud. Lakukan seperti itu dalam setiap shalatmu.â€', '(HR. Bukhari no. 793 dan Muslim no. 397).*', NULL, NULL, 'ilustrasi orang sholat menggunakan aksesoris balapan', NULL, 1510613390, 4, 1510613390, 4),
('Maling Sholat. Paling Jahat Paling Nekat', 30, 'Musnad Imam Ahmad dari Nabi shallallahu â€˜alaihi wasallam, bahwa beliau bersabda,\nâ€œSejahat-jahat pencuri adalah yang mencuri dari shalatnyaâ€. Para sahabat bertanya, â€œWahai Rasulullah, bagaimana mencuri dari sholat?â€. Rasulullah berkata, â€œDia tidak sempurnakan ruku dan sujudnyaâ€', '(HR Ahmad no 11532, dishahihkan oleh al Albani dalam Shahihul Jamiâ€™ 986)  Sumber: https://muslim.or.id/13845-tumaninah-dalam-shalat-1.html', NULL, NULL, 'ilustrasi maling kartun', NULL, 1510615181, 4, 1510615181, 4),
('Ini Sholat Bang. Bukan Balap', 31, 'Nabi shalallahu â€˜alaihi wa sallam, â€œHendaklah takut salah seorang di antara kalian, apabila ia mengangkat kepalanya sebelum imam melakukannya, Allah akan menjadikan kepalanya kepala keledai atau suara keledai atau bentuk fisik yang menyerupai keledai. (Mutafaq â€˜alaihi).', NULL, NULL, 'GH shalat khusyuk.jpg', '', 1510665453, 1510615878, 4, 1510665453, 2),
('keledai/keÂ·leÂ·dai/ nomina: 1. binatang berkuku satu, mirip kuda kecil, bertelinga panjang dengan ekor yang hanya pada ujungnya berbulu; \n2. kiasan: orang bodoh', 32, 'Nabi shalallahu â€˜alaihi wa sallam, â€œHendaklah takut salah seorang di antara kalian, apabila ia mengangkat kepalanya sebelum imam melakukannya, Allah akan menjadikan kepalanya kepala keledai atau suara keledai atau bentuk fisik yang menyerupai keledai. (Mutafaq â€˜alaihi)', NULL, NULL, NULL, '', NULL, 1510616602, 4, 1510617653, 4),
('Curhat Sama Allah. Jangan Sama Orang. Apalagi Suami/Istri Orang', 33, 'Dari â€˜Abdullah bin â€˜Amir, yaitu Ibnu Rabiâ€™ah, dari bapaknya, Rasulullah shallallahu â€˜alaihi wa sallam bersabda,\n\nâ€œJanganlah seorang laki-laki berduaan dengan seorang wanita yang tidak halal baginya karena sesungguhnya syaithan adalah orang ketiga di antara mereka berdua kecuali apabila bersama mahramnya.â€ (HR. Ahmad no. 15734. Syaikh Syuâ€™aib Al Arnauth mengatakan hadits ini shahih lighairihi)\nDi antara bentuk berdua-duaan (alias: khalwat) adalah chating dengan lawan jenis, termasuk bentuknya curhat dengan suami orang.', NULL, NULL, 'GH BW rose.jpg', 'ilustrasi black withered rose', 1510638871, 1510617861, 4, 1510638871, 2);
